import React, { useState } from 'react';
import './post.css';

import { Users } from '../../dummyData';

const Post = ({ post }) => {
  const [likes, setLikes] = useState(post.like);
  const [isLiked, setIsLiked] = useState(false);

  //	PUBLIC FOLDER to make dir easy to track
  const PF = process.env.REACT_APP_PUBLIC_FOLDER;

  //	destructure user and filter person for each post
  const { id, desc, photo, date, userId, like, comment } = post;
  const user = Users.filter((user) => user.id === userId);

  //	like
  const likeHandler = () => {
    setLikes(isLiked ? likes - 1 : likes + 1);
    setIsLiked(!isLiked);
  };

  return (
    <div className='post' id={id}>
      <div className='postWrapper'>
        <div className='postTop'>
          <div className='postTopLeft'>
            <img
              src={user[0].profilePicture}
              className='postProfileImg'
              alt=''
            />
            <span className='postUsername'>{user[0].username}</span>
            <span className='postDate'>{date}</span>
          </div>
          <div className='postTopRight'></div>
        </div>

        <div className='postCenter'>
          <span className='postText'>{post?.desc}</span>
          <img src={PF + photo} alt='' className='postImg' />
        </div>
        <div className='postBottom'>
          <div className='postBottomLeft'>
            <img
              className='likeIcon'
              onClick={likeHandler}
              src='/assets/like.png'
              alt=''
            />
            <span className='postLikeCounter'>
              <strong>{likes}</strong>{' '}
              {isLiked && `You and ${likes - 1} others`}
            </span>
          </div>
          <div className='postBottomRight'>
            <span className='postCommentText'>{comment} comments</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Post;
